import React, { Component } from 'react';
import { render } from 'react-dom';


import '../style/winLoseDraw.css';
import Score from './Score';

class WinLoseDraw extends Component {
    state = {
        wld: '',
    }



    test = () => {
        if (this.props.winLoseDraw === 'Win') {
            this.setState({
                wld: 'win',
            })
        }
        if (this.props.winLoseDraw === 'Lose') {
            this.setState({
                wld: 'lose',
            })
        }
        if (this.props.winLoseDraw === 'Draw') {
            this.setState({
                wld: 'draw',
            })
        }

    }

    render() {
        return (
            <div className='wld'>
                {this.props.wld}
            </div>
        )
    }

}

export default WinLoseDraw;
