import React from 'react';

import '../style/table.css';

import PlaceForCards from './PlaceForCards';

const Table = (props) => {

    let counter = 0;
    const firstCards = [];

    const giveFirstThirdCard = () => {

        for (let i = 0; i < 3; i++) {
            counter++
            firstCards.push(props.cards[counter])
        }
    }



    return (
        <div className='table'>
            <PlaceForCards
                firstThird={giveFirstThirdCard}
            />
            <button onClick={giveFirstThirdCard}></button>
        </div>)
}

export default Table;
